<style scoped>
.gallery {
    background-color: var(--secondary-black);
    border-radius: 20px;

    .gallery-nav {
        display: flex;
        justify-content: space-between;
        min-width: 362px;
        border-radius: 20px 20px 0 0;

        .gallery-nav_link {
            cursor: pointer;
            width: 35%;
            text-align: center;
            padding-block: 20px;
            background-color: var(--secondary-black);

            &:not(:last-child) {
                position: relative;

                &::before {
                    content: '';
                    position: absolute;
                    width: 1px;
                    height: 80%;
                    background-color: var(--text);
                    top: 50%;
                    right: 0;
                    transform: translateY(-50%);
                }
            }

            &:first-child {
                border-radius: 20px 0 0 0;
            }

            &:last-child {
                border-radius: 0 20px 0 0;
            }
        }
    }

    .gallery-image {
        display: grid;
        grid-template-columns: repeat(4, 1fr);
        padding: 15px;
        gap: 3px;
    }
}
</style>